import React, { useState } from 'react';
import FormPage1 from './FormPage1';
import FormPage2 from './FormPage2';
import FormPage3 from './FormPage3';

function App() {
    const [currentPage, setCurrentPage] = useState('home');  // Track current page

    const renderPage = () => {
        switch (currentPage) {
            case 'firstname':
                return <FormPage1 />;
            case 'lastname':
                return <FormPage2 />;
            case 'username':
                return <FormPage3 />;
            default:
                return (
                    <div>
                        <h1>Welcome to Home Page</h1>
                        <button onClick={() => setCurrentPage('firstname')}>Go to Firstname Form</button>
                        <button onClick={() => setCurrentPage('lastname')}>Go to Lastname Form</button>
                        <button onClick={() => setCurrentPage('username')}>Go to Username Form</button>
                    </div>
                );
        }
    };

    return (
        <div className="App">
            {renderPage()}  {/* Render the selected page */}
        </div>
    );
}

export default App;
