import React, { useState } from 'react';
import axios from 'axios';

const FormPage1 = () => {
    const [firstname, setFirstname] = useState('');
    const [username, setUsername] = useState('');
    const [lastname, setLastname] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.post('http://localhost:8000/react-items/', { firstname })
            .then(res => console.log('Data submitted successfully'))
            .catch(err => console.error('Error submitting form', err));
    };

    return (
        <div>
            <h2>Enter Firstname</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Firstname:
                    <input type="text" value={firstname} onChange={e => setFirstname(e.target.value)}/>
                </label>
            </form>

            <h2>Enter Lastname</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Lastname:
                    <input type="text" value={lastname} onChange={e => setLastname(e.target.value)}/>
                </label>
            </form>

            <h2>Enter Firstname</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Username:
                    <input type="text" value={username} onChange={e => setUsername(e.target.value)} />
                </label>
                <button type="submit">Submit</button>
            </form>

            {/* Display success or error messages */}
            {this.state.successMessage && <p style={{ color: 'green' }}>{this.state.successMessage}</p>}
            {this.state.errorMessage && <p style={{ color: 'red' }}>{this.state.errorMessage}</p>}

        </div>
    );
};

export default FormPage1;
