import React, { useState } from 'react';  // Ensure React and useState are imported
import axios from 'axios';
import { createBrowserRouter, RouterProvider } from 'react-router-dom';
import './App.css';
import FormPage1 from './FormPage1';
import FormPage2 from './FormPage2';

function App() {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    firstname: '',
    lastname: '',
    first_name: '',
    last_name: '',
    email: '',
    project_manager: false,
  });

  const [statusMessage, setStatusMessage] = useState(null);

  const handleSubmit = () => {
    console.log('Form Submitted', formData);

    axios.post('http://localhost:8000/user-accounts/', formData)
      .then(response => {
        console.log('Data saved successfully:', response.data);
        setStatusMessage('Data saved successfully!');
      })
      .catch(error => {
        console.error('There was an error!', error);
        setStatusMessage('Error saving data! Please try again.');
      });
  };

  // Initialize the router
  const router = createBrowserRouter([
    {
      path: "/formpage1",
      element: <FormPage1 formData={formData} setFormData={setFormData} handleSubmit={handleSubmit} />,
    },
    {
      path: "/formpage2",
      element: <FormPage2 formData={formData} setFormData={setFormData} handleSubmit={handleSubmit} />,
    },
    {
      path: "*",  // Fallback route
      element: <div>Page not found</div>,
    },
  ]);

  return (
    <div className="App">
      <h1>Form Navigation</h1>

      {/* RouterProvider renders the routes */}
      <RouterProvider router={router} />

      {statusMessage && (
        <div style={{ marginTop: '20px', color: statusMessage.includes('Error') ? 'red' : 'green' }}>
          {statusMessage}
        </div>
      )}
    </div>
  );
}

export default App;
